// Helper component to handle image errors gracefully
const MediaItem: React.FC<{ src: string; alt: string; className?: string }> = ({ src, alt, className }) => {
  const [hasError, setHasError] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    setHasError(false);
    setIsLoaded(false);
  }, [src]);

  const handleError = () => {
    console.warn(`Failed to load media: ${src}`);
    setHasError(true);
  };

  const handleLoad = () => {
    setIsLoaded(true);
  };

  const isVideo = (url: string) => {
    if (!url) return false;
    const cleanUrl = url.split('?')[0].toLowerCase();
    return cleanUrl.endsWith('.mp4') || cleanUrl.endsWith('.mov') || cleanUrl.endsWith('.webm');
  };

  // If there's an error, show the stylish placeholder with DEBUG info
  if (hasError) {
    const filename = src.startsWith('http') ? 'External Link' : src;
    return (
      <div className={`bg-neutral-900 border border-neutral-800 flex flex-col items-center justify-center text-center p-8 ${className}`}>
        <div className="w-8 h-8 mb-4 opacity-20 text-neutral-500">
           <svg fill="currentColor" viewBox="0 0 24 24"><path d="M21 19V5c0-1.1-.9-2-2-2H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2zM8.5 13.5l2.5 3.01L14.5 12l4.5 6H5l3.5-4.5z"/></svg>
        </div>
        <span className="text-[10px] text-red-500 font-mono tracking-widest uppercase mb-1">Image Missing</span>
        <code className="text-[10px] text-neutral-500 max-w-[90%] break-all">{filename}</code>
        <p className="text-[9px] text-neutral-600 mt-2 px-4">
          Check if file exists in <b>public/images/</b>
        </p>
      </div>
    );
  }

  return (
    <div className={`relative ${className} bg-neutral-900`}>
      {!isLoaded && !isVideo(src) && (
        <div className="absolute inset-0 flex items-center justify-center bg-neutral-900 z-0">
          <div className="w-6 h-6 border-2 border-neutral-700 border-t-neutral-400 rounded-full animate-spin"></div>
        </div>
      )}

      {isVideo(src) ? (
        <video
          src={src}
          className={`w-full h-full object-cover`}
          muted
          loop
          autoPlay
          playsInline
          onError={handleError}
          onLoadedData={handleLoad}
        />
      ) : (
        <img
          src={src}
          alt={alt}
          className={`w-full h-full object-cover transition-opacity duration-500 ${isLoaded ? 'opacity-100' : 'opacity-0'}`}
          onError={handleError}
          onLoad={handleLoad}
        />
      )}
    </div>
  );
};

