interface ComparisonRowProps {
  item: typeof COMPARISONS[0];
  reverse: boolean;
}

const ComparisonRow: React.FC<ComparisonRowProps> = ({ item, reverse }) => {
  // Simple slider mechanism
  const [sliderPosition, setSliderPosition] = useState(50);
  const [renderError, setRenderError] = useState(false);
  const [realError, setRealError] = useState(false);

  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = Math.max(0, Math.min(e.clientX - rect.left, rect.width));
    const percent = Math.max(0, Math.min((x / rect.width) * 100, 100));
    setSliderPosition(percent);
  };

  const handleTouchMove = (e: React.TouchEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const touch = e.touches[0];
    const x = Math.max(0, Math.min(touch.clientX - rect.left, rect.width));
    const percent = Math.max(0, Math.min((x / rect.width) * 100, 100));
    setSliderPosition(percent);
  }

  return (
    <div className={`flex flex-col ${reverse ? 'lg:flex-row-reverse' : 'lg:flex-row'} items-center gap-12`}>
      {/* Comparison Slider */}
      <div className="w-full lg:w-2/3 select-none relative group cursor-ew-resize"
           onMouseMove={handleMouseMove}
           onTouchMove={handleTouchMove}
      >
         <div className="relative w-full aspect-[4/3] md:aspect-[16/9] overflow-hidden bg-neutral-900 border border-neutral-800">
            {/* Real Image (Base) */}
            {!realError ? (
                <img 
                    src={item.realImage} 
                    alt="Reality" 
                    className="absolute inset-0 w-full h-full object-cover pointer-events-none"
                    onError={() => setRealError(true)}
                />
            ) : (
                <div className="absolute inset-0 flex items-center justify-center bg-neutral-800 text-neutral-600 text-xs">Image Missing: {item.realImage}</div>
            )}

             {/* Label Real */}
             <div className="absolute bottom-4 right-4 bg-black/50 backdrop-blur px-3 py-1 text-xs text-white border border-white/20 pointer-events-none">
                è½åœ°å®æ‹ / REALITY
            </div>

            {/* Render Image (Overlay) */}
            <div 
                className="absolute inset-0 overflow-hidden border-r border-white/50 bg-neutral-800"
                style={{ width: `${sliderPosition}%` }}
            >
                {!renderError ? (
                    <div className="absolute inset-0 w-full h-full pointer-events-none" style={{ width: `${100 / (sliderPosition / 100)}%` }}>
                       <img 
                            src={item.renderImage} 
                            alt="Render" 
                            className="w-full h-full object-cover"
                            onError={() => setRenderError(true)}
                        />
                    </div>
                ) : (
                     <div className="absolute inset-0 flex items-center justify-center bg-neutral-700 text-neutral-400 text-xs" style={{ width: `${100 / (sliderPosition / 100)}%` }}>
                        Image Missing: {item.renderImage}
                     </div>
                )}

                 <div className="absolute bottom-4 left-4 bg-white/90 text-black px-3 py-1 text-xs font-bold pointer-events-none">
                    æ•ˆæœå›¾ / RENDER
                </div>
            </div>

            {/* Slider Handle */}
            <div 
                className="absolute top-0 bottom-0 w-[2px] bg-white cursor-ew-resize z-10 flex items-center justify-center shadow-[0_0_10px_rgba(0,0,0,0.5)]"
                style={{ left: `${sliderPosition}%` }}
            >
                <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center shadow-lg">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={2} stroke="currentColor" className="w-4 h-4 text-black">
