import React, { useState, useEffect } from 'react';

interface MediaItemProps {
  src: string;
  alt: string;
  className?: string;
}

const MediaItem: React.FC<MediaItemProps> = ({ src, alt, className = "" }) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(false);

  // 这里的逻辑确保如果本地路径 (./images/...) 失败，会显示一个艺术感的占位
  const placeholderUrl = "https://images.unsplash.com/photo-1634017839464-5c339ebe3cb4?q=80&w=1000&auto=format&fit=crop";

  useEffect(() => {
    setIsLoaded(false);
    setError(false);
  }, [src]);

  return (
    <div className={`relative overflow-hidden bg-neutral-900 ${className}`}>
      {/* Loading Skeleton */}
      {!isLoaded && !error && (
        <div className="absolute inset-0 z-0 animate-pulse bg-neutral-800 flex items-center justify-center">
          <div className="w-4 h-4 rounded-full border border-white/20 border-t-white animate-spin"></div>
        </div>
      )}

      <img
        src={error ? placeholderUrl : src}
        alt={alt}
        className={`w-full h-full object-cover transition-all duration-1000 ease-out ${
          isLoaded ? 'opacity-100 scale-100' : 'opacity-0 scale-110'
        }`}
        onLoad={() => setIsLoaded(true)}
        onError={() => setError(true)}
        loading="lazy"
      />

      {/* Subtle Overlay */}
      <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent pointer-events-none" />
    </div>
  );
};

export default MediaItem;

