const ComparisonSlider: React.FC<{ item: typeof COMPARISONS[0] }> = ({ item }) => {
  const [position, setPosition] = useState(50);
  const containerRef = useRef<HTMLDivElement>(null);

  const handleMove = (e: React.MouseEvent | React.TouchEvent) => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const x = 'touches' in e ? e.touches[0].clientX : (e as React.MouseEvent).clientX;
    const relativeX = Math.max(0, Math.min(x - rect.left, rect.width));
    setPosition((relativeX / rect.width) * 100);
  };

  return (
    <div className="flex flex-col gap-12">
      <div 
        ref={containerRef}
        className="relative w-full aspect-[16/9] bg-neutral-900 cursor-ew-resize overflow-hidden"
        onMouseMove={handleMove}
        onTouchMove={handleMove}
      >
        {/* Real Image (Background) */}
        <div className="absolute inset-0">
          <img src={item.realImage} alt="Reality" className="w-full h-full object-cover" />
          <div className="absolute bottom-6 right-6 bg-black/40 backdrop-blur-md px-4 py-1 border border-white/10 text-[10px] text-white tracking-widest uppercase">
            Reality / 实拍
          </div>
        </div>

        {/* Render Image (Foreground) */}
        <div 
          className="absolute inset-0 border-r border-white/30"
          style={{ width: `${position}%` }}
        >
          <div className="absolute inset-0 w-[100vw] h-full" style={{ width: containerRef.current?.offsetWidth }}>
            <img src={item.renderImage} alt="Render" className="w-full h-full object-cover grayscale" />
          </div>
          <div className="absolute bottom-6 left-6 bg-white/90 px-4 py-1 text-[10px] text-black font-bold tracking-widest uppercase">
            Render / 效果
          </div>
        </div>

        {/* Slider Handle */}
        <div 
          className="absolute top-0 bottom-0 z-10 w-[1px] bg-white"
          style={{ left: `${position}%` }}
        >
          <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-10 h-10 rounded-full border border-white/50 backdrop-blur-sm flex items-center justify-center bg-black/20 group hover:bg-white transition-colors">
            <div className="flex gap-1">
              <div className="w-1 h-1 rounded-full bg-white group-hover:bg-black" />
              <div className="w-1 h-1 rounded-full bg-white group-hover:bg-black" />
              <div className="w-1 h-1 rounded-full bg-white group-hover:bg-black" />
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-12 gap-6 items-start">
        <div className="md:col-span-4">
          <h4 className="text-2xl text-white font-bold uppercase tracking-tight">{item.title}</h4>
        </div>
        <div className="md:col-span-8">
          <p className="text-neutral-500 font-light leading-relaxed max-w-2xl">
