import React, { useState, useEffect } from 'react';

interface MediaItemProps {
  src: string;
  alt: string;
  className?: string;
}

const MediaItem: React.FC<MediaItemProps> = ({ src, alt, className = "" }) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(false);

  // 这里的逻辑确保如果录入的本地路径或链接失败，会显示一个艺术感的占位
  const placeholderUrl = "https://images.unsplash.com/photo-1634017839464-5c339ebe3cb4?q=80&w=1000&auto=format&fit=crop";

  useEffect(() => {
    // 每次 src 改变时重置状态
    setIsLoaded(false);
    setError(false);

    // 预加载逻辑
    const img = new Image();
    img.src = src;
    img.onload = () => setIsLoaded(true);
    img.onerror = () => setError(true);
  }, [src]);

  return (
    <div className={`relative overflow-hidden bg-neutral-900 ${className}`}>
      {/* 骨架屏加载状态 */}
      {!isLoaded && !error && (
        <div className="absolute inset-0 z-0 bg-neutral-900 flex items-center justify-center">
           <div className="flex flex-col items-center gap-4">
