import React, { useState, useEffect, useRef } from 'react';

interface MediaItemProps {
  src: string;
  alt: string;
  className?: string;
  autoPlay?: boolean;
  loop?: boolean;
  muted?: boolean;
}

const MediaItem: React.FC<MediaItemProps> = ({ 
  src, 
  alt, 
  className = "", 
  autoPlay = true, 
  loop = true, 
  muted = true 
}) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);

  // 识别是否为视频
  const isVideo = src.match(/\.(mp4|webm|ogg|mov)$|^blob:|^data:video/i);

  // 兜底图
  const placeholderUrl = "https://images.unsplash.com/photo-1634017839464-5c339ebe3cb4?q=80&w=1000&auto=format&fit=crop";

  useEffect(() => {
    setIsLoaded(false);
    setError(false);

    if (!isVideo) {
      const img = new Image();
      img.src = src;
      img.onload = () => setIsLoaded(true);
      img.onerror = () => setError(true);
    }
  }, [src, isVideo]);

  const handleVideoLoad = () => setIsLoaded(true);
  const handleVideoError = () => setError(true);

  return (
    <div className={`relative overflow-hidden bg-neutral-900 aspect-video ${className}`}>
      {/* 骨架屏加载状态 */}
      {!isLoaded && !error && (
        <div className="absolute inset-0 z-0 bg-neutral-900 flex items-center justify-center">
           <div className="flex flex-col items-center gap-4">
              <div className="w-8 h-[1px] bg-neutral-700 animate-[width_2s_infinite]"></div>
              <span className="text-[8px] font-mono text-neutral-700 tracking-[0.5em] uppercase">Processing Media</span>
           </div>
        </div>
      )}

      {isVideo ? (
        <video
          ref={videoRef}
          src={src}
          className={`w-full h-full object-cover transition-opacity duration-1000 ${
            isLoaded ? 'opacity-100' : 'opacity-0'
          }`}
          autoPlay={autoPlay}
          loop={loop}
          muted={muted}
          playsInline
          onLoadedData={handleVideoLoad}
          onError={handleVideoError}
        />
      ) : (
        <img
          src={error ? placeholderUrl : src}
          alt={alt}
          className={`w-full h-full object-cover transition-all duration-[1200ms] ease-[cubic-bezier(0.23,1,0.32,1)] ${
            isLoaded ? 'opacity-100 scale-100 blur-0' : 'opacity-0 scale-105 blur-lg'
          }`}
          loading="lazy"
          style={{ imageRendering: 'auto' }}
        />
      )}

      {/* 艺术感蒙层 */}
      <div className="absolute inset-0 bg-gradient-to-t from-black/20 via-transparent to-transparent pointer-events-none" />
      <div className="absolute inset-0 border border-white/5 pointer-events-none" />
    </div>
  );
};

